import React, { useState } from "react";

function CreateArea() {
  const [note, setNote] = useState({
    title: "",
    content: "",
  });

  function handleChange(event) {
    const content = event.target.value;
    setNote(content);
  }

  return (
    <div>
      <form>
        <input
          onChange={handleChange}
          value={note}
          name="title"
          placeholder="Title"
        />
        <textarea
          onChange={handleChange}
          name="content"
          placeholder="Take a note..."
          rows="3"
          value={note}
        />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
